import UIKit
import Swinject

// MARK: Inject all scene dependencies

class LandingDependencyInjector: SwinjectViewControllerDependencyInjector<LandingViewController> {
    
    // MARK: Registration
    
    override class func registerDependencies(container: Container) {
        container.register(LandingViewController.self) { (resolver, viewController:LandingViewController) -> LandingViewController in
            let interactor = resolver.resolve(LandingInteractor.self)!
            let presenter = LandingPresenter()
            let router = LandingRouter()
            viewController.interactor = interactor
            viewController.router = router
            interactor.presenter = presenter
            presenter.viewController = viewController
            router.viewController = viewController
            router.dataStore = interactor
            return viewController
        }
    }
}
